function mutation(arr) {
  let firstWord = arr[0].toLowerCase();
  let secondWord = arr[1].toLowerCase();
  secondWord.split("");
  for(let i = 0; i < secondWord.length; i++) {
    console.log("secondWord[i] " + secondWord[i] + " // " + secondWord[i][0]);
    if(firstWord.indexOf(secondWord[i][0]) < 0) {
      return false;
    }
    else if(firstWord.indexOf(secondWord[i][0]) >= 0 && i === secondWord.length - 1) {
      return true;
    }
  }
}

mutation(["hello", "hey"]);
