function frankenSplice(arr1, arr2, n) {
  // It's alive. It's alive!
  let begArr = arr2.slice(0, n);
  let endArr = arr2.slice(n, arr2.length);
  let frankenArr = [];
  for (let i = 0; i < begArr.length; i++) {
    frankenArr.push(begArr[i]);
  }
  for (let j = 0; j < arr1.length; j++) {
    frankenArr.push(arr1[j]);
  }
  for (let k = 0; k < endArr.length; k++) {
    frankenArr.push(endArr[k]);
  }
  return frankenArr;

}

frankenSplice([1, 2, 3], [4, 5, 6], 1);
