function titleCase(str) {
  let tempArr = str.split(" ");
  let titleCasedStr = "";
  let lowerCaseStr = "";
  let firstLetter = "";
  for(let i = 0; i < tempArr.length; i++) {
    firstLetter = tempArr[i][0].toUpperCase();
    lowerCaseStr = (tempArr[i].substring(1,
    tempArr[i].length)).toLowerCase();
    titleCasedStr += firstLetter + lowerCaseStr + " ";
  };
  titleCasedStr = titleCasedStr.substring(0, 
  titleCasedStr.length - 1);
  return titleCasedStr;
}

