function getIndexToIns(arr, num) {
  // Find my place in this sorted array.
  let originalArr = arr;
  let sortedArr = arr;
  let copyEl;
  let sorted = false;
  let properIndex = 0;

  // Sort an array
  while(sorted === false) {
    for(let i = 0; i < sortedArr.length; i++) {
      if(sortedArr[i] > sortedArr[i + 1]) {
        copyEl = sortedArr.splice(i, 1);
        sortedArr.push(copyEl);
        i = 0;
      }
      if(i === sortedArr.length - 1) {
        sorted = true;
      }
    }
  }

  console.log("sortedArr___ >> " + sortedArr + " // NUM // " + num);

  // Establish index
  for(let y = 0; y < sortedArr.length; y++) {
    if(num > sortedArr[y] && num <= sortedArr[y + 1]) {
      properIndex = y + 1;
      break;
    }
    if(num >= sortedArr[y] && y === sortedArr.length - 1) {
      properIndex = sortedArr.length;
    }
  }

 console.log("properIndex__ >> " + properIndex);

  return properIndex;

}

