function convertHTML(str) {
  // &colon;&rpar;
  let chars = str.split("");
  let htmlStr = "";
  console.log(chars);
  for(let i = 0; i < chars.length; i++) {
    if(chars[i] === "&") {
      htmlStr += "&amp;";
    }
    else if(chars[i] === "<") {
      htmlStr += "&lt;";

    }
    else if(chars[i] === ">") {
      htmlStr += "&gt;";

    }
    else if(chars[i] === "\"") {
      htmlStr += "&quot;";

    }
    else if(chars[i] === "'") {
      htmlStr += "&apos;";

    }
    else {
      htmlStr += chars[i];
    }
  }
  return htmlStr;
}

convertHTML("Dolce & Gabbana");
