function diffArray(arr1, arr2) {
  let newArr = [];
  let same = false;
  // Same, same; but different.
  // If one array is empty
  if(arr1.length <= 0) {
    newArr = newArr.concat(arr2);
    return newArr;
  }
  if(arr2.length <= 0) {
    newArr = newArr.concat(arr1);
    return newArr;
  }
  // Compare arr1 to arr2
  for(let i = 0; i < arr1.length; i++) {
    for(let y = 0; y < arr2.length; y++) {
      same = false;
      if(arr1[i] === arr2[y]) {
        same = true;
        break;
      }
      if(same === false && y === arr2.length - 1) {
        newArr.push(arr1[i]);
      }
    }
  }
  // Compare arr2 to arr1 && check if value is in newArr
  for(let x = 0; x < arr2.length; x++) {
    for(let z = 0; z < arr1.length; z++) {
      same = false;
      if(arr2[x] === arr1[z]) {
        same = true;
        break;
      }
      if(same === false && z === arr1.length - 1 && newArr.indexOf(arr2[x]) < 0) {
        newArr.push(arr2[x]);
      }
    }
  }
  return newArr;
}

diffArray([1, 2, 3, 5], [1, 2, 3, 4, 5]);
