function orbitalPeriod(arr) {
  var GM = 398600.4418;
  var earthRadius = 6367.4447;
  var objectAlt = 0;
  var orbitalArr = [];
  var orbitalPeriod = 0;
  for (var i = 0; i < arr.length; i++) {
    objectAlt = arr[i]["avgAlt"];
    var orbitalPeriod = Math.round(2 * Math.PI * Math.sqrt(Math.pow(earthRadius + objectAlt, 3) / GM));
    orbitalArr.push({name: arr[i]["name"], orbitalPeriod: orbitalPeriod});
  }
  return orbitalArr;
}

