function translatePigLatin(str) {
  let tempStr = "";
  let counter = 0;
  // If str without vowels
  if((/^([^aeiouAEIOU0-9\W]+)$/).test(str)) {
    return str + "ay";
  }
  // Str has vowels & vowel is first
  if((/^[aeiou]/).test(str)) {
    return str + "way";
  }
  // Str has vowels & consonant (cluster) is first
  if(!(/^[aeiou]/).test(str)) {
    for(let i = 0; i < str.length; i++) {
      if(!(/[aeiou]/).test(str[i])) {
        counter++;
      }
      else if((/[aeiou]/).test(str[i])) {
        break;
      }
    }
    tempStr = str.substr(0, counter);
    str = str.substr(counter);
    str = str + tempStr + "ay";
    return str;
  }
}

translatePigLatin("xxx");
