function myReplace(str, before, after) {
  let whatCase1 = 0;
  let whatCase2 = 0;
  let indexBefore = 0;
  let replacedStr = "";
  let tempStr = "";
  // Check first letter case in before
  if (before[0] === before[0].toUpperCase()) {
    whatCase1  = 1;
  }
  else if (!(before[0] === before[0].toUpperCase())) {
    whatCase1 = 0;
  }
  // Check first letter case in after
  if (after[0] === after[0].toUpperCase()) {
    whatCase2  = 1;
  }
  else if (!(after[0] === after[0].toUpperCase())) {
    whatCase2 = 0;
  }
  // Change first letter case of after if necessary
  if(whatCase1 !== whatCase2) {
    if (whatCase1 === 1) {
      after = after.replace(after.charAt(0), after[0].toUpperCase());
    }
    if (whatCase1 === 0) {
      after = after.replace(after.charAt(0), after[0].toLowerCase());
    }
  }
  // Find word in str
  indexBefore = str.indexOf(before);
  // Delete before word form the str and add after word
  replacedStr = str.slice(0, indexBefore);
  tempStr = str.slice(indexBefore + before.length, str.length);
  replacedStr = replacedStr + after + tempStr;
  return replacedStr;
}

myReplace("He is Sleeping on the couch", "Sleeping", "sitting");
