function destroyer(arr) {
  // Remove all the values
  let theArr = arguments[0];
  let delArgs = [];
  let finalArr = [];
  let same = false;
  // Add arguments to delete into an array
  for(let i = 1; i < arguments.length; i++) {
    delArgs.push(arguments[i]);
  }
  // Delete delArgs form theArr
  for(let y = 0; y < theArr.length; y++) {
    for(let x = 0; x < delArgs.length; x++) {
      let same = false;
      if(theArr[y] === delArgs[x]) {
        let same = true;
        break;
      }
      if(same === false && x === delArgs.length - 1) {
        finalArr.push(theArr[y]);
      }
    }
  }
  return finalArr;
}

