function uniteUnique(arr) {
  let unitedArr = [];
  for(let i = 0; i < arguments.length; i++) {
    for(let y = 0; y < arguments[i].length; y++) {
      if(!unitedArr.includes(arguments[i][y])) {
        unitedArr.push(arguments[i][y]);
      }
    }
  }
  return unitedArr;
}

uniteUnique([1, 3, 2], [5, 2, 1, 4], [2, 1]);
