function rot13(str) { // LBH QVQ VG!
    var strEncoded = str;
    var arrDecoded = [];
    var strDecoded = "";
    var uni = 0;
    // Create string with uncode letter values
    for(var i = 0; i < strEncoded.length; i++){
        uni = strEncoded.charCodeAt(i);
        // If letter value is between 65 and 77 add 13 to the value
        if(uni >= 65 && uni <= 77){
            uni+= 13;
        }
        // If letter value is between 78 and 90 substract 13 from the value
        else if(uni >= 78 && uni <= 90){
            uni-= 13;
        }
        arrDecoded.push(uni);
    }
    // Change unicode to letters
    for (var i = 0; i < arrDecoded.length; i++){
        strDecoded += String.fromCharCode(parseInt(arrDecoded[i]));
    }
    return strDecoded;
}

