function convertToRoman(num) {
  let thousands = 0;
  let houndreds = 0;
  let tens = 0;
  let unities = 0;
  let roman = "";
  // Break num to smaller parts
  if(num >= 1000) {
    thousands = Math.floor(num / 1000);
  }
  if(num >= 100) {
    houndreds = Math.floor((num - thousands * 1000) / 100);
  }
  if(num >= 10) {
    tens = Math.floor((num - thousands * 1000 - houndreds * 100) / 10); 
  }
  if(num <= 9) {
    unities = num;
  }
  else {
    unities = Math.floor((num - thousands * 1000 - houndreds * 100 - tens * 10));
  }
// Convert to roman
// Thousands
if(thousands > 0) {
  for(let t = 0; t < thousands; t++) {
    roman += "M";
  }
}
// Houndreds
if(houndreds > 0) {
  if(houndreds <= 3) {
    for(let h = 0; h < houndreds; h++) {
      roman += "C";
    }
  }
  else {
    switch (houndreds) {
      case 4:
        roman += "CD";
        break;
      case 5:
        roman += "D";
        break;
      case 6:
        roman += "DC";
        break;
      case 7:
        roman += "DCC";
        break;
      case 8:
        roman += "DCCC";
        break;
      case 9:
        roman += "CM";
        break;
      default:
        roman += "";
    }
  }
}
// Tens
if(tens > 0) {
  if(tens <= 3) {
    for(let t = 0; t < tens; t++) {
      roman += "X";
    }
  }
  else {
    switch (tens) {
      case 4:
        roman += "XL";
        break;
      case 5:
        roman += "L";
        break;
      case 6:
        roman += "LX";
        break;
      case 7:
        roman += "LXX";
        break;
      case 8:
        roman += "LXXX";
        break;
      case 9:
        roman += "XC";
        break;
      default:
        roman += "";
    }
  }
}
// Unities
if(unities > 0) {
  if(unities <= 3) {
    for(let u = 0; u < unities; u++) {
      roman += "I";
    }
  }
  else {
    switch (unities) {
      case 4:
        roman += "IV";
        break;
      case 5:
        roman += "V";
        break;
      case 6:
        roman += "VI";
        break;
      case 7:
        roman += "VII";
        break;
      case 8:
        roman += "VIII";
        break;
      case 9:
        roman += "IX";
        break;
      default:
        roman += "";
    }
  }
}
return roman;
}

